import ReactMapGL from 'react-map-gl'
import React, {useState} from 'react'
import './MostPopular.css'
import MapGL, {NavigationControl, Marker} from 'react-map-gl';
import {MdLocationOn} from 'react-icons/md'
import {Popup} from 'react-map-gl'

export default function MostPopular() {

    const [viewport, setViewport] = useState({
        latitude: 42.667542,
        longitude: 21.166191,
        width: "100vw",
        height: "80vh",
        zoom: 10
    });



    const [selectedCity, setSelectedCity] = useState(null)
    return (
        <div  id="map" style={{backgroundColor: "white"}}>
            <h1>Popular Cities in Kosovo</h1>
      <ReactMapGL {...viewport}  mapboxApiAccessToken={process.env.REACT_APP_MAPBOX_TOKEN} 
      mapStyle="mapbox://styles/sejlaali/cjy3s8cw02yzg1cnxos5r713z"
      onViewportChange={
          viewport => {
              setViewport(viewport);
          }}>
        <Marker
        longitude={20.8660}
        latitude={42.891}
        >
            <div onClick={e => {
                e.preventDefault();
                setSelectedCity("Mitrovica")
            }} style={{fontSize: "30px", color: "#caa14e"}}><MdLocationOn /></div>
        </Marker>
        <Marker
        longitude={21.1655}
        latitude={42.6629}>
            <div 
            onClick={e => {
                e.preventDefault();
                setSelectedCity("Pristina")
            }}
            style={{fontSize: "30px", color: "#caa14e"}}><MdLocationOn /></div>
        </Marker>
        <Marker
        longitude={20.7415}
        latitude={42.2153}>
            <div 
            onClick={e => {
                e.preventDefault();
                setSelectedCity("Prizren")
            }}
            style={{fontSize: "30px", color: "#caa14e"}}><MdLocationOn /></div>
        </Marker>
        <Marker
        longitude={20.2887}
        latitude={42.6593}
        >
        <div  onClick={e => {
            e.preventDefault();
            setSelectedCity("Peja")
        }} style={{fontSize: "30px", color: "#caa14e"}}><MdLocationOn /></div>
        </Marker>

        {selectedCity === "Mitrovica" ? <Popup><div>"According to the 2011 Census, in Mitrovica live 84,235 inhabitants, 71,909 of which in the southern municipality and 12,326 in North Mitrovica."</div></Popup> : null}
      </ReactMapGL>
</div>
    )
}
